import React from 'react';
import pic from '../images/welcome.jpg';
import XMLData from '../files/cd_catalog.xml';
class MainPage extends React.Component {
    render() {
        return <div className="section-main">
              <div className="row">
                <div className="leftcolumn">
                    <div className="card">
                        <h1>TITLE HEADING</h1>
                        <h2>Title description, Dec 7, 2017</h2>
                        <h3>Asma Batool</h3>
                        <summary>
                        <div className="fakeimg"><img src={pic} alt="logo" /></div>
                        </summary>
                         <p>Sunt in culpa qui officia deserunt mollit anim id est laborum consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco.</p>
                    </div>
                </div>
            </div>
            </div>;
    }

    loadXMLDoc() {
        var xmlhttp = new XMLHttpRequest();
        xmlhttp.onreadystatechange = function() {
          if (this.readyState == 4 && this.status == 200) {
            myFunction(this);
          }
        };
        xmlhttp.open("GET", XMLData, true);
        xmlhttp.send();
    }
     myFunction(xml) {
        var i;
        var xmlDoc = xml.responseXML;
        var table="";
        var x = xmlDoc.getElementsByTagName("post");
        for (i = 0; i <x.length; i++) { 
          table += "<div><div><div><h1>" +
          x[i].getElementsByTagName("title")[0].childNodes[0].nodeValue +
          "</h1><h2>" +
          x[i].getElementsByTagName("date")[0].childNodes[0].nodeValue +
          "</h2><h3>" + 
          x[i].getElementsByTagName("author")[0].childNodes[0].nodeValue +
          "</h3><summary> " + 
          x[i].getElementsByTagName("image_path")[0].childNodes[0].nodeValue +
          "</summary><p>" + 
          x[i].getElementsByTagName("TITLE")[0].childNodes[0].nodeValue +
          "</p>";

        }
        document.getElementById("demo").innerHTML = table;
      }
}
export default MainPage;
